mapper.select:
select * from s_dict


        parent_code is null


        and parent_code = #{parentCode}


        and parent_id is null


        and parent_id = #{parentId}


        and label like concat('%', #{label}, '%')


        and code like concat('%', #{code}, '%')


        and status = #{status}


ORDER BY orders ASC, create_time DESC

select * from s_dict


        id = #{id}


        and code = #{code}
